            'picture' => 'http://therobotcenterapp.com/PortadasCursos/CursoDefault.png',
            'editor' => '<p><span style="color:#000099"><span style="font-size:26px"><span style="font-family:Arial,Helvetica,sans-serif"><strong>PREGUNTAR E IMAGINAR</strong></span></span></span></p>

