            'editor' => '<p style="text-align:center"><span style="font-size:20px"><span style="font-family:Arial,Helvetica,sans-serif"><strong>TEMA DE CLASE: DISE&Ntilde;O DE ADITAMENTOS&nbsp;</strong></span></span></p>

