'editor' => '<p style="text-align:right"><span style="font-family:Arial,Helvetica,sans-serif"><img alt="" src="http://therobotcenterapp.com/laravel-filemanager/photos/18/Logotipo Eduktronic.png" style="height:49px; width:100px" /></span></p>

