'editor' => '<p><span style="font-family:Arial,Helvetica,sans-serif"><a href="http://therobotcenterapp.com/laravel-filemanager/photos/253/LOGO EDUKTRONIC.PNG" target="_blank"><img alt="" src="http://therobotcenterapp.com/laravel-filemanager/photos/253/LOGO EDUKTRONIC.PNG" style="float:right; height:47px; width:100px" /></a></span></p>

