'editor' => '<p style="text-align:center"><img alt="" src="http://therobotcenterapp.com/laravel-filemanager/photos/254/6.PNG" style="float:left; height:140px; width:300px" /></p>

<p style="text-align:center">&nbsp;</p>

<p style="text-align:center">&nbsp;</p>

<p style="text-align:center">&nbsp;</p>

