'name' => 'Estacionamiento automático',
'description' => NULL,
'picture' => 'http://therobotcenterapp.com/PortadasCursos/CursoDefault.png',
'editor' => '<p><img alt="" src="http://therobotcenterapp.com/laravel-filemanager/photos/139/LogosSec/Eduktronic.PNG" style="height:54px; width:111px" /></p>

