'editor' => '<p style="text-align:center"><strong><span style="color:#ffffff"><span style="font-size:26px"><span style="font-family:Arial,Helvetica,sans-serif"><span style="background-color:#f1c40f">PREGUNTAR E IMAGINAR&nbsp;&nbsp;</span></span></span></span></strong></p>

