'picture' => NULL,
'editor' => '<p><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:11pt"><strong><span style="font-size:19.5pt"><span style="color:#f1c40f">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>

