'description' => NULL,
'picture' => NULL,
'editor' => '<p><span style="font-size:26px"><span style="font-family:Arial,Helvetica,sans-serif"><span style="background-color:white"><strong><span style="color:#f1c40f">PREGUNTAR E IMAGINAR</span></strong></span></span></span></p>

