'editor' => '<p style="text-align:center"><span style="color:#ffffff"><span style="font-size:26px"><span style="font-family:Arial,Helvetica,sans-serif"><span style="background-color:#f1c40f">PREGUNTAR E IMAGINAR</span></span></span></span></p>

