// (21, 'S1.4.3', 'REINOS', 'EL ALUMNO CONOCERÁ LAS SIMILITUDES ENTRE ANIMALES Y ROBOTS ZOOMORFOS', NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:black\">&iquest;C&oacute;mo se pueden agrupar los diferentes seres vivos para su estudio?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:white\">CLASE 31</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:black\">(IM&Aacute;GENES DE LOS 5 REINOS)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:black\">Para conocer un poco m&aacute;s sobre los diferentes grandes Reinos, ingresa a la siguiente p&aacute;gina de internet:</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:black\"><a href=\"http://www.educa.jcyl.es/educacyl/cm/gallery/Recursos%20Infinity/aplicaciones/web_conocimiento/seresvivos/juega.htmhttp:/www.youtube.com/watch?V=6NIR57uPk3i\" style=\"color:blue; text-decoration:underline\" target=\"_blank\">ttp://www.educa.jcyl.es/educacyl/cm/gallery/Recursos%20Infinity/aplicaciones/web_conocimiento/seresvivos/juega.htmhttp://www.youtube.com/watch?V=6NIR57uPk3i</a></span></span></span></span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Si no puedes ingresar a la p&aacute;gina de internet, puedes guiarte con la siguiente informaci&oacute;n.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(IMAGEN DE INFORMACI&Oacute;N)</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Cu&aacute;les son los diferentes grupos en que se pueden clasificar a los seres vivos?</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Cu&aacute;les son las principales caracter&iacute;sticas de cada uno de estos reinos?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(TABLA DE CARACTERISTICAS)</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;De qu&eacute; reino forman parte los reptiles?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">El ensamble de esta clase corresponde a un Reptil. Reparte con tus compa&ntilde;eros de equipo el trabajo.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(TABLA DE HERRAMIENTAS DE PROGRAMACI&Oacute;N)</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Describe el funcionamiento de los mecanismos que permiten que el reptil se mueva?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;El ensamble del reptil se parece a alg&uacute;n animal que conozcas? &iquest;A cu&aacute;l?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Cu&aacute;les son las principales caracter&iacute;sticas que diferencian a los reptiles de los dem&aacute;s animales?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Modifica la programaci&oacute;n de tu reptil para que al sentir una presencia cerca, reproduzca un sonido y de una mordida.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-04 20:07:48', '2019-07-30 00:09:19'),
// (22, 'S1.4.4', 'INTELIGENCIA ARTIFICIAL', 'EL ALUMNO CONOCERÁ LOS PRINCIPIOS BÁSICOS DE PROGRAMACIÓN PARA LA TOMA DE DECISIONES', NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Resuelve los siguientes ejercicios.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(Imagen de ejercicios mentales x2)</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Pudiste resolver correctamente los ejercicios? &iquest;Por qu&eacute;?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Sabes qu&eacute; es la inteligencia?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">La inteligencia se define como la capacidad para resolver problemas. Todos los seres humanos tenemos esta capacidad, aunque algunos en mayor o menor medida debido a su desarrollo o estimulaci&oacute;n.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Es importante mencionar que la inteligencia es diferente de la sabidur&iacute;a, la cual es la acumulaci&oacute;n de conocimientos.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Desde que se comenzaron a construir las primeras computadoras, se ha buscado el poder dotar a las m&aacute;quinas de las aptitudes necesarias para que puedan realizar tareas humanas, incluyendo el pensar y razonar, con lo cual se ha dado pie al desarrollo de la inteligencia artificial.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">La inteligencia artificial es la capacidad mediante la cual, las m&aacute;quinas o robots pueden realizar tareas humanas, como resoluci&oacute;n de problemas, discriminar objetos, responder preguntas, etc. esto es posible gracias al desarrollo de las redes neuronales, las cuales mediante el uso de circuitos el&eacute;ctricos y programas avanzados buscan copiar el funcionamiento del cerebro de manera electr&oacute;nica.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Dentro del campo de la rob&oacute;tica, la Inteligencia Artificial se ha aplicado en el funcionamiento de robots humanoides llamados androides o robots de tipo zoom&oacute;rfico (en forma de animales). </span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">La actividad para esta clase ser&aacute; ensamblar y programar un perro guardi&aacute;n inteligente, que cuidar&aacute; la casa. Las acciones que deber&aacute; realizar el perro guardi&aacute;n son:</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Girar sobre su propio eje en busca de alg&uacute;n desconocido. En cuanto detecte la presencia de alg&uacute;n extra&ntilde;o deber&aacute; dejar de girar y avanzar en l&iacute;nea recta hacia donde el intruso este, al llegar a una distancia de 3 cm del intruso, ladrar&aacute; y mostrar&aacute; una cara de enojo, para posteriormente regresar a su lugar y comenzar su rutina.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;C&oacute;mo se da cuenta tu perro guardi&aacute;n que hay alg&uacute;n intruso?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; necesitar&iacute;a tu perro guardi&aacute;n para diferenciar a un conocido de un intruso?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Conoces alguna aplicaci&oacute;n de la Inteligencia Artificial? &iquest;Cu&aacute;l?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Modifica tu ensamble para que al sentir que lo acaricias, reproduzca el sonido de una risa y muestre una carita feliz.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-04 20:30:53', '2019-07-30 00:09:57'),
// (23, 'S1.4.5', 'CIRUGÍA ROBÓTICA', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:black\">CIRUGIA ROB&Oacute;TICA</span></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Normalmente estamos acostumbrados a que cuando hablamos de robots, pensemos en industrias o f&aacute;bricas. Lo que es una realidad, es que el uso de robots no s&oacute;lo abarca estas &aacute;reas. Podemos encontrarlos en muchas m&aacute;s, como en los hogares, de compa&ntilde;&iacute;a, artistas o en la medicina como observaremos a continuaci&oacute;n.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Robot Quir&uacute;rgico Da Vinci</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Duraci&oacute;n: 5:24 min.</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><a href=\"https://www.youtube.com/watch?v=nD0M9QGGD9w\" style=\"color:#0563c1; text-decoration:underline\" target=\"_blank\">https://www.youtube.com/watch?v=nD0M9QGGD9w</a></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Para qu&eacute; se ocupa el robot que se muestra en el video?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; ventajas ofrece su uso sobre la manera habitual de operar?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Si fuera necesario &iquest;Permitir&iacute;as que un robot te operar&aacute;? &iquest;Por qu&eacute;?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Un brazo quir&uacute;rgico es un aparato que permite realizar operaciones de manera m&aacute;s precisa y exacta. El brazo es controlado a distancia por un m&eacute;dico, lo cual permite que dado el caso, el doctor pueda no estar presente en la sala de operaci&oacute;n, ofreciendo una nueva ventaja a la medicina.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Actividad: Ensamblar un brazo quir&uacute;rgico que ser&aacute; utilizado para sacar diferentes piezas que se encontrar&aacute;n dentro de la figura de un cuerpo humano.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;En qu&eacute; se parece su brazo quir&uacute;rgico al Robot Da Vinci mostrado en el video?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">El ensamble que realizaron, no utiliza sensores. &iquest;C&oacute;mo los implementar&iacute;as para mejorar tu ensamble?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; otros tipos de operaciones pueden llevarse a cabo utilizando robots? Describe c&oacute;mo se utilizar&iacute;an.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Agrega dos sensores de tacto al brazo quir&uacute;rgico, modifica la programaci&oacute;n para que al presionar un sensor se cierre el brazo y al presionar el otro sensor, se abra.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, NULL, '2019-07-04 20:50:08', '2019-07-30 00:10:28'),
// (24, 'S1.4.6', 'PROTESIS ROBOTICAS', 'EL ALUMNO CONOCERÁ LOS BENEFICIOS DE LA BIÓNICA Y SUS APLICACIONES', NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Observa con atenci&oacute;n la siguiente imagen.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(IMAGEN DE PERSONA CON PROTESIS ROBOTICA)</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Cu&aacute;l crees que sea el tema principal de la imagen?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; son las pr&oacute;tesis?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; son las pr&oacute;tesis?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Seg&uacute;n el diccionario de la Real Academia Espa&ntilde;ola, pr&oacute;tesis se define como Pieza, aparato o sustancia que se coloca en el cuerpo para mejorar alguna de sus funciones, o con fines est&eacute;ticos.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Como se mencionaba en clases anteriores, la rob&oacute;tica se encuentra presente en muchas &aacute;reas de la vida cotidiana. Dentro del campo m&eacute;dico no solamente se ocupa para realizar operaciones, sino tambi&eacute;n participa en el funcionamiento y desarrollo de pr&oacute;tesis que permiten a las personas llevar una vida m&aacute;s funcional reincorporando a los individuos a la vida laboral y cotidiana.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Existen varios tipos de pr&oacute;tesis, dependiendo del uso que se les dar&aacute;, se pueden clasificar seg&uacute;n la zona del cuerpo que sustituyen, por el material con el que est&aacute;n construidas o por su funcionamiento (Mec&aacute;nico, el&eacute;ctrico, neum&aacute;tico).</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(TABLA DE HERRAMIENTAS DE PROGRAMACI&Oacute;N)</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">SE INDICA QUE SE CONSTRUYA UNA PROTESIS</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; ventajas tiene el uso de pr&oacute;tesis?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; caracter&iacute;sticas consideras que deben tener las pr&oacute;tesis para que puedan ser totalmente funcionales? Describe.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">De acuerdo a la clasificaci&oacute;n descrita anteriormente &iquest;Qu&eacute; tipo de pr&oacute;tesis ensamblaron?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Agrega nuevos sensores al funcionamiento de la pr&oacute;tesis.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, NULL, '2019-07-04 21:03:27', '2019-07-30 00:10:54');
// INSERT INTO `courses` (`id`, `code`, `name`, `description`, `picture`, `editor`, `teacher_id`, `category_id`, `level_id`, `created_at`, `updated_at`) VALUES
// (25, 'S1.5.1', '¿QUÉ ES UN TORNEO DE ROBÓTICA?', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;C&oacute;mo es un torneo de rob&oacute;tica? Vamos a observar el siguiente video para conocer c&oacute;mo son los torneos de rob&oacute;tica realizados actualmente.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">World Robotic Olimpiad Nacional 2013 Lego Education M&eacute;xico</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Duraci&oacute;n: 5:31 minutos</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><a href=\"https://www.youtube.com/watch?v=9JsdzO7sZjk\" style=\"color:#0563c1; text-decoration:underline\" target=\"_blank\">https://www.youtube.com/watch?v=9JsdzO7sZjk</a></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">(IMAGEN DE WRO)</span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Alrededor de todo el mundo como en nuestro pa&iacute;s se llevan a cabo numerosos torneos de rob&oacute;tica, que abarcan diferentes tem&aacute;ticas y est&aacute;n dirigidos para asistentes de todas las edades.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iexcl;Existen torneos regionales, nacionales y hasta mundiales!</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Algunos de estos torneos son: WRO, First Lego League, Robocup, Torneo Mexicano de Rob&oacute;tica, Copa Internacional de Rob&oacute;tica del IPN.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Su objetivo es el de acercar a las nuevas generaciones al uso de las tecnolog&iacute;as y su implementaci&oacute;n, esto bajo un ambiente sano y de esparcimiento.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Durante este mes entrenaremos para participar en un torneo de rob&oacute;tica interno que realizaremos al final del bloque, por lo que cada clase practicaremos algo diferente.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Lo primero que hay que definir despu&eacute;s de inscribirnos a una competencia es el robot que utilizaremos.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">El reto de esta clase ser&aacute; dise&ntilde;ar un robot que medir&aacute; m&aacute;ximo 25 cm x 25 cm, el robot debe poder avanzar, retroceder y dar giros de 360&deg;, para realizarlo deber&aacute;n platicar en equipo acerca de las caracter&iacute;sticas que quieren que tenga su robot y que cumpla con las caracter&iacute;sticas.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">En equipos repartan los siguientes roles y realicen las actividades correspondientes.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; complicaciones tuvieron al realizar su robot?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;La estructura de su robot le permiti&oacute; realizar los retos de programaci&oacute;n?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; podr&iacute;an mejorarle a su robot?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Una vez listo su robot, se deber&aacute; agregar sonido y un mensaje en la pantalla con las siguientes especificaciones:</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Durante todo el trayecto deber&aacute; aparecer en la pantalla el nombre de su equipo.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Al inicio debe realizar un sonido que indique que la programaci&oacute;n va a iniciar y al final debe realizar otro sonido diferente que indique que la programaci&oacute;n ha terminado.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-04 21:31:01', '2019-07-30 00:11:41'),
// (26, 'S1.5.2', 'DISEÑO DE ADITAMENTOS', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; son los aditamentos y para qu&eacute; sirven?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Un aditamento es una cosa o accesorio que se a&ntilde;ade para complementar algo.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">En una competencia de rob&oacute;tica normalmente se utilizan aditamentos que consigan complementar al robot y logren cumplir con los retos establecidos. Un brazo mec&aacute;nico, unas pinzas, una garra o una rampa, ser&iacute;an algunos aditamentos que podr&iacute;an tener los robots.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Estos son ejemplos de algunos aditamentos:</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(IM&Aacute;GENES DE ADITAMENTOS)</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">El d&iacute;a de hoy se construir&aacute; un aditamento para su robot. Dicho aditamento deber&aacute; ayudar al robot a atrapar o recoger una pelota. El objetivo ser&aacute; que su robot avance, recoja la pelota y la lleve consigo al punto de salida. El equipo que consiga primero atrapar la pelota y llevarla al punto de salida obtendr&aacute; m&aacute;s puntos.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Antes de empezar a construir, deber&aacute;n platicar en equipo y dise&ntilde;ar las caracter&iacute;sticas de su aditamento. </span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Nota: Se les permitir&aacute; realizar el robot base y &uacute;nicamente deber&aacute;n enfocarse en dise&ntilde;ar el aditamento.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Funcion&oacute; su aditamento?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; mecanismo tiene su aditamento?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; podr&iacute;an mejorarle a su aditamento?</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Modifica tu programaci&oacute;n para que tu robot realice un sonido cuando el robot haya atrapado la pelota</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-04 21:40:36', '2019-07-30 00:12:06'),
// (27, 'S1.5.3', 'SENSOR ULTRASONICO', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Recuerdas este sensor?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Escribe su nombre:</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Describe su funcionamiento:</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">El reto del d&iacute;a de hoy ser&aacute; agregar un sensor ultras&oacute;nico para conseguir que el robot detecte la pelota y se detenga</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(IMAGEN SENSOR ULTRASONICO)</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">.</span><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Utiliza el ensamble base y agrega el sensor ultras&oacute;nico para que pueda detenerse el robot cuando encuentre la pelota.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Tu robot fue capaz de detenerse?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;D&oacute;nde tuvieron que colocar el sensor y por qu&eacute;?</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Cuando el robot encuentre la pelota deber&aacute; cambiarla de lugar, puede empujarla o atraparla. Agrega un sonido y una imagen despu&eacute;s de que el sensor encuentre la pelota.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-04 21:57:37', '2019-07-30 00:12:33'),
// (28, 'S1.5.4', 'SEGUIDOR DE LÍNEA', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Esta clase deber&aacute;n conseguir que su robot siga una l&iacute;nea, observa el siguiente v&iacute;deo que ejemplifica la actividad del d&iacute;a de hoy: <strong><a href=\"https://www.youtube.com/watch?v=AYU1UuDbsJo\" style=\"color:#0563c1; text-decoration:underline\" target=\"_blank\">https://www.youtube.com/watch?v=AYU1UuDbsJo</a></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Una vez que observen el video respondan lo siguiente:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; sensor utiliza el robot?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Analicen el movimiento del robot &iquest;Por qu&eacute; creen que el robot se mueve en zigzag?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; debe hacer el robot cuando el sensor ve algo blanco? &iquest;Qu&eacute; motor debe mover?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; debe hacer el robot cuando el sensor ve algo negro? &iquest;Qu&eacute; motor debe mover?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">A partir de lo anterior describe c&oacute;mo consideran que debe quedar la programaci&oacute;n:</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">.</span><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">MODELO BASE</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Tu robot fue capaz de detenerse?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;D&oacute;nde tuvieron que colocar el sensor y por qu&eacute;?</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Consigue que tu robot sea lo m&aacute;s veloz posible.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-04 22:10:16', '2019-07-30 00:13:02'),
// (29, 'S1.5.5', 'ENTRENANDO PARA EL TORNEO', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Lleg&oacute; el momento de aplicar todo lo aprendido durante el bloque, el d&iacute;a de hoy vamos a realizar un entrenamiento para el torneo, el reto del torneo ser&aacute; realizar un robot que sea capaz de seguir la l&iacute;nea, detectar la pelota, atraparla y llevarla al lugar de inicio, el equipo que agarre primero la pelota ser&aacute; el que tenga m&aacute;s Puntos.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Van a organizarse para desarrollar el robot con el aditamento necesario para tomar la pelota</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Contesta lo siguiente:</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;El robot que dise&ntilde;aron les ayuda a cumplir el reto?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Los sensores est&aacute;n bien colocados?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;La programaci&oacute;n que hicieron cumple con el reto?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Qu&eacute; le podr&iacute;an mejorar a su robot?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Modifica tu ensamble o tu robot para que sea m&aacute;s veloz.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Es el momento de realizar el torneo. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Est&aacute;n listos?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Las reglas ser&aacute;n las siguientes:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Participar&aacute;n dos equipos, se pondr&aacute;n de frente y la pelota estar&aacute; al centro.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Una vez que se indique deber&aacute;n dar play a la programaci&oacute;n y no se podr&aacute; tocar al robot mientras est&eacute; en competencia.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">El primer equipo que lleve la pelota dentro del &aacute;rea de inicio ser&aacute; el equipo ganador, o bien.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Si ning&uacute;n equipo logra llevar la pelota al inicio ganar&aacute; el equipo que acumule m&aacute;s puntos.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Puntos asignados por cada reto logrado:</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(TABLA DE REGISTRO)</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Es hora de construir tu robot.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Una vez realizada la competencia escribe tu experiencia en ella:</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&iquest;Te gustar&iacute;a participar en alguna competencia internacional de rob&oacute;tica?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Realicen las mejoras para realizar el recorrido en el menor tiempo posible.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, NULL, '2019-07-04 22:22:06', '2019-07-30 00:13:33'),
// (30, 'S1.5.6', 'INTRODUCCIÓN II', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">NOMBRE DE DISPOSITIVOS ELECTRONICOS</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(IM&Aacute;GENES DE DISPOSITIVOS ELECTRONICOS)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(RECUADRO PARA ESCIBIR NOMBRE Y FUNCI&Oacute;N DE LOS DISPOSITIVOS)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">RETO: En equipo, construyan un robot que avance sin ruedas al menos 40 cm. El dise&ntilde;o es libre, no habr&aacute; gu&iacute;as. Si todos los equipos lo logran, se realizar&aacute; una competencia de velocidad en un trayecto de un metro.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">FORMULARIO DE PROGRAMACI&Oacute;N</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Hoy realizaremos un repaso en las funciones que nos permiten programar aspectos m&aacute;s detallados.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">En equipo dise&ntilde;a, construye y programa un robot que pueda moverse solo:</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">En un patr&oacute;n que describa de forma repetible un cuadrado o un tri&aacute;ngulo. Tu programaci&oacute;n deber&aacute; incluir una funci&oacute;n de repetici&oacute;n (loop) &oacute; de bifurcaci&oacute;n.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Si ya lo logr&oacute; tu equipo el patr&oacute;n, anexa m&aacute;s elementos como: sensores, sonidos y colores para cada movimiento que se incluyan en la programaci&oacute;n de tu robot.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">(IM&Aacute;GENES DE UN CUADRODO Y UN TRIANGULO) </span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Ve con atenci&oacute;n el siguiente video.</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Duraci&oacute;n: 3:05 minutos</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#e93440\"><a href=\"https://www.youtube.com/watch?v=NCwBkNgPZFQ\" style=\"color:blue; text-decoration:underline\">https://www.youtube.com/watch?v=NCwBkNgPZFQ</a></span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Cu&aacute;l es el tema principal del video?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; actividades de la vida cotidiana se han visto afectadas o modificadas por los avances tecnol&oacute;gicos?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Podr&iacute;as vivir sin hacer uso de la tecnolog&iacute;a? &iquest;Por qu&eacute;?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Pero, &iquest;Qu&eacute; es la tecnolog&iacute;a?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">La palabra tecnolog&iacute;a es de origen griego, formada por t&eacute;chne que es arte, oficio o destreza y por logos, que es estudio de.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">De acuerdo a la Real Academia Espa&ntilde;ola, la definici&oacute;n de tecnolog&iacute;a es: &quot;Conjunto de teor&iacute;as y de t&eacute;cnicas que permiten el aprovechamiento pr&aacute;ctico del conocimiento cient&iacute;fico.&quot;</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">En base a la definici&oacute;n anterior, escribe 10 ejemplos de tecnolog&iacute;a que utilizamos en la vida diaria</span></span><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">:</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">Un ejemplo de tecnolog&iacute;a aplicada son los materiales con los que trabajaras durante tus clases de rob&oacute;tica a lo largo del ciclo escolar y con los cuales podr&aacute;s ser un part&iacute;cipe activo dentro del campo tecnol&oacute;gico.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">Para comenzar a familiarizarte con el material, realizaras las siguientes actividades. Abre tu set EV3 y observa el tipo de piezas que se encuentran contenidas en la charola roja superior.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Dibuja 5 de estas piezas y escribe sus nombres.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">En la parte inferior de tu set, se encuentran los componentes electr&oacute;nicos. Identif&iacute;calo y escribe su nombre en la l&iacute;nea que corresponda.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">En equipos de 3 personas deber&aacute;n ensamblar y programar un robot que avance en l&iacute;nea recta, y el cual participar&aacute; en una carrera de velocidad contra los robots de los dem&aacute;s equipos.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ed7d31\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; avances tecnol&oacute;gicos puedes identificar en tu robot?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; m&aacute;quinas podr&iacute;as construir y programar utilizando el set ev3?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; opinas del uso desmedido de aparatos tecnol&oacute;gicos?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Cu&aacute;nto tiempo al d&iacute;a utilizas alg&uacute;n aparato tecnol&oacute;gico?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;C&oacute;mo podr&iacute;as utilizar solo el tiempo necesario los aparatos tecnol&oacute;gicos?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Modifiquen su robot agregando un sensor que le permita detectar obst&aacute;culos y evitarlos.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">T&Eacute;CNOLOGIA: <span style=\"color:#201c20\">Conjunto de conocimientos, saberes, experiencias, t&eacute;cnicas a partir de las cuales el ser humano cambia, transforma, utiliza su entorno para la creaci&oacute;n de nuevas herramientas, m&aacute;quinas y productos para satisfacer sus necesidades.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">La palabra tecnolog&iacute;a proviene del griego tekhn&eacute; = arte, t&eacute;cnica u oficio y de logos = estudio, conocimiento</span></span><span style=\"font-size:13.5pt\"><span style=\"color:#201c20\">.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, NULL, '2019-07-04 23:53:17', '2019-07-30 00:14:02'),
// (31, 'S2.1.2', 'PROGRAMACIÓN EN PARALELO', 'EL ALUMNO CONOCERÁ COMO UTILIZAR EL BLOQUE DE EJECUTAR PARA QUE REALICE DIVERSAS FUNCIONES', NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Observa el siguiente video</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">La multitarea - ELSA PUNSET</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Duraci&oacute;n: 6:25 minutos</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#e93440\"><a href=\"https://www.youtube.com/watch?v=PKCAS4ARHhQ\" style=\"color:blue; text-decoration:underline\">https://www.youtube.com/watch?v=PKCAS4ARHhQ</a></span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;De qu&eacute; trata el video que acabas de ver?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; es la multitarea?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;C&oacute;mo podr&iacute;a un robot realizar multitareas?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Normalmente las programaciones que realizas son lineales, donde los bloques de programaci&oacute;n se ejecutan de izquierda a derecha de manera ordenada. </span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para que un bloque pueda ejecutarse, es necesario que su antecesor se haya ejecutado por completo antes.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN DE BLOQUES DE PROGRAMACI&Oacute;N)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;C&oacute;mo ser&iacute;a necesario realizar la programaci&oacute;n si se desea llevar a cabo varias tareas al mismo tiempo?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Observa la siguiente imagen.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN DE PROGRAMACI&Oacute;N)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; acciones realizan las dos programaciones anteriores?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Cu&aacute;l se ejecuta primero?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">La programaci&oacute;n anterior es un ejemplo de programaci&oacute;n en paralelo, la cual permite realizar dos o m&aacute;s acciones al mismo tiempo y de manera independiente, es decir, no dependen de que se ejecute una por completo para ejecutarse las dem&aacute;s.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El ensamble de esta clase corresponde a un robot que simula que toca una guitarra. Las acciones que deber&aacute; llevar a cabo ser&aacute;n mover su brazo que toca la guitarra, reproducir una canci&oacute;n y mostrar una imagen en la pantalla.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para poder reproducir la canci&oacute;n, es necesario utilizar el bloque sonido, el cual reproducir&aacute; una nota musical por cada bloque.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN DE BLOQUE SONIDO)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Completa la tabla de Herramientas de programaci&oacute;n de acuerdo a las diferentes acciones que debe llevar a cabo tu robot. Recuerda realizar un algoritmo y pseudoc&oacute;digo para cada programaci&oacute;n.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN DE TABLA SOBRE HERRAMIENTAS DE PROGRAMACI&Oacute;N)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ed7d31\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Observen la programaci&oacute;n de su ensamble.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Por qu&eacute; hay varias l&iacute;neas de programaci&oacute;n?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; pasar&iacute;a si form&aacute;ramos en l&iacute;nea todas las programaciones?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; sucede si no colocamos el bloque de inicio en cada l&iacute;nea de programaci&oacute;n?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; ventajas y desventajas hay en el uso de este tipo de programaci&oacute;n en paralelo?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Modifica tu programaci&oacute;n y ensamble para agregarle un nuevo motor.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">PROGRAMACI&Oacute;N EN PARALELO: </span></span><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Forma de computo en que se ejecutan varias instrucciones de manera simult&aacute;nea, partiendo del principio &quot;Divide y Vencer&aacute;s&quot; en el que problemas grandes se pueden dividir en m&aacute;s peque&ntilde;os.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, NULL, '2019-07-05 00:16:27', '2019-07-30 00:17:31');
// INSERT INTO `courses` (`id`, `code`, `name`, `description`, `picture`, `editor`, `teacher_id`, `category_id`, `level_id`, `created_at`, `updated_at`) VALUES
// (32, 'S2.1.3', 'CARRERA DE BARRILES', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:black\">CARRRERA DE BARRILES</span></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">&iquest;Conoces las carreras de barriles?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">Observa el siguiente video</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">LAS 10 MEJORES CARRERAS DE BARRILES ARENA</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">CLUB 2014</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"color:#201c20\">Duraci&oacute;n: 4:27 minutos</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"color:#e93440\"><a href=\"https://www.youtube.com/watch?v=hDuHBNSglwQ\" style=\"color:blue; text-decoration:underline\" target=\"_blank\">https://www.youtube.com/watch?v=hDuHBNSglwQ</a></span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;En qu&eacute; consiste la carrera de barriles?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Conoces alg&uacute;n otro juego o deporte parecido? &iquest;Cu&aacute;l?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Si tuvieras que participar en una carrera de barriles, pero con robots caballos, &iquest;Qu&eacute; instrucciones le dar&iacute;as para ganar?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Sab&iacute;as que existe un sensor que puede ayudarte a que tu robot realice giros m&aacute;s precisos?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El girosensor, es un sensor que puede detectar movimiento rotacional y &aacute;ngulos de rotaci&oacute;n total en grados, lo que puede ayudar a que el robot pueda dar giros m&aacute;s precisos o al detectar cuando una parte del robot est&aacute; girando puede indicar que est&aacute; cayendo.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Este sensor act&uacute;a entones, en una de sus modalidades como un transportador que te muestra la medida en &aacute;ngulos de un giro.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Una caracter&iacute;stica de este sensor que debes tomar en cuenta es que va acumulando los valores que va midiendo. Por lo que es necesario debes reiniciarlo al comienzo de cada programaci&oacute;n. Para hacerlo, ocupa el bloque &quot;Girosensor&quot; de la pesta&ntilde;a &quot;Sensores&quot; (Color amarillo) y elige &quot;Reiniciar&#39;:</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para programarlo se ocupar&aacute; el bloque de espera con girosensor/Comparar/ &Aacute;ngulo como se muestra en la siguiente imagen.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN DE CONFIGURACI&Oacute;N) </span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Construye y programa junto con tus compa&ntilde;eros de equipo un caballo rob&oacute;tico que pueda participar en una carrera de barriles.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Con ayuda de un masking, marca tres &quot;X&quot; en el piso de un &aacute;rea de tu sal&oacute;n de clase que se encuentre despejada o en el patio.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Cada x representar&aacute; un barril. Tu robot deber&aacute; rodear cada una de ellas en el menor tiempo posible.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Est&aacute;s listo?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN DE HERRAMIENTA DE PROGRAMACI&Oacute;N) </span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Gui&aacute;ndote con las herramientas de programaci&oacute;n, realiza el programa para que tu robot caballo participe en la carrera de barriles.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ed7d31\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; ventajas ofrece el trabajar con el girosensor cuando se programan giros?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; diferencias notas entre programar los giros desde el bloque &quot;?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Mover direcci&oacute;n&#39;: &quot;Mover tanque&quot; y &quot;Espera con girosensor&quot;?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;En qu&eacute; otros robots o m&aacute;quinas ocupar&iacute;as el girosensor? Explica c&oacute;mo funcionar&iacute;a en ellos</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El caballo robot participara en un desfile alrededor de un ruedo hexagonal que mide 2 rotaciones por lado. Realiza la programaci&oacute;n necesaria para que pueda hacerlo.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Girosensor: Sensor que puede detectar movimiento rotacional y &aacute;ngulo de rotaci&oacute;n total.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, NULL, '2019-07-05 00:40:06', '2019-07-30 00:18:25'),
// (33, 'S2.1.4', 'CARRERA DE RELEVO', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El d&iacute;a de hoy vamos a realizar una carrera de relevos &iquest;has participado en una? &iquest;C&oacute;mo son?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">La carrera de relevos es una prueba de velocidad donde los integrantes del equipo tienen que transportar de uno en uno, un objeto llamado estafeta a lo largo de una distancia determinada, el equipo que lleve primero la estafeta a la meta, es el ganador.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Normalmente, el equipo ganador es aquel en el que sus Integrantes corren con mayor velocidad que sus contrincantes.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Recuerdas c&oacute;mo se puede medir la velocidad? &iquest;Cu&aacute;l es su f&oacute;rmula?</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para calcular la velocidad, es necesario conocer la distancia y el tiempo en que se recorri&oacute;. En base a estos datos podemos obtenerla ocupando la siguiente f&oacute;rmula.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN DE FORMULA DE VELICIDAD) </span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Lo que equivale a: </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Velocidades igual a distancia sobre tiempo. Por ejemplo, Si un corredor llev&oacute; la estafeta a lo largo de 100 metros y tard&oacute; 10 segundos en hacerlo <strong>&iquest;A qu&eacute; velocidad iba? </strong></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Datos: Distancia =100 m, tiempo = 10 segundos, Sustituimos en la f&oacute;rmula</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">V= 100m/10s</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">V= 10 m/s</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El corredor iba a una velocidad de 10 m/s</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El d&iacute;a de hoy realizaremos una carrera de relevos y calcularemos las velocidades de los participantes. Nuestra carrera de relevos ser&aacute; con coches. Cada coche tendr&aacute; dos sensores de tacto, uno al frente y uno atr&aacute;s.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para simular que los coches se pasan la estafeta, deber&aacute;n tocar los sensores entre ellos. El primer coche deber&aacute; avanzar y detenerse al chocar con la parte trasera del siguiente auto, cuando el segundo auto reciba el toque en el sensor de tacto, deber&aacute; comenzar a avanzar para alcanzar al siguiente auto y as&iacute; hasta llegar a la meta. Ganar&aacute; el equipo que logre pasar la l&iacute;nea de meta primero.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;C&oacute;mo funciona el sensor de tacto?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Recordemos que nuestro sensor de tacto permite cerrar un circuito que le da informaci&oacute;n a nuestro robot para realizar alguna acci&oacute;n. El sensor </span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Puede estar en alguno de los 3 estados siguientes:</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&bull;PRESIONADO: cuando se presiona el bot&oacute;n rojo</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&bull;LIBERADO: cuando no est&aacute; presionado el bot&oacute;n rojo</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&bull;EN CONTACTO: espera a que se cumplan las dos acciones, presionado y luego liberado.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Cada equipo (de 3 integrantes) deber&aacute; construir su propio carro para participar en la carrera de relevos.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">NOTA: </span></span><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para la competencia de relevos necesitamos hacer equipo con otros coches, es decir, vamos a aliarnos con otros equipos para hacer nuestro EQUIPO DE RELEVOS. Todos los equipos de relevo deben tener el mismo n&uacute;mero de coches.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Marquen el punto de salida, los lugares donde se colocar&aacute;n los relevos y la meta.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN HERRAMIENTA DE PROGRAMACI&Oacute;N)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ed7d31\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Una vez realizada la competencia, la repetiremos, pero esta vez mediremos el tiempo en que cada coche recorre su tramo, as&iacute; como la distancia que recorri&oacute;. Con estos datos van a calcular la velocidad a la que se desplazaron los coches de su equipo.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(TABLA DE REGISTRO)</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Modifica tu programaci&oacute;n para que tu robot realice un sonido cuando se presionen los sensores.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-05 01:02:57', '2019-07-30 05:08:29'),
// (34, 'S2.1.5', 'TRAYECTORIA DE COLORES', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:black\">TRAYECTORIA DE COLORES</span></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; es una trayectoria? &iquest;Qu&eacute; es el desplazamiento?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para conocer m&aacute;s acerca de estos conceptos vamos a observar el siguiente video.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Cinem&aacute;tica 3D: Trayectoria, Distancia y Desplazamiento:</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Duraci&oacute;n: 4:27 minutos</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#e93440\"><a href=\"https://www.youtube.com/watch?v=kXa3BRRdlH\" style=\"color:#0563c1; text-decoration:underline\">https://www.youtube.com/watch?v=kXa3BRRdlH</a></span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Despu&eacute;s de observar el video contesta:</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; es la trayectoria?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; es desplazamiento?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Cu&aacute;l es la f&oacute;rmula para obtener el desplazamiento?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">En el video &iquest;Qu&eacute; fue mayor, la trayectoria o el desplazamiento?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El d&iacute;a de hoy vamos a descubrir m&aacute;s acerca de la trayectoria y el desplazamiento. </span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para realizar la actividad del d&iacute;a de hoy van a utilizar el sensor de color.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El sensor de color puede funcionar de tres maneras como detector de luz reflejada, detector de luz ambiental y como detector de color.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Este sensor puede distinguir entre 7 colores: negro, verde, rojo, amarillo, blanco, azul y marr&oacute;n, adem&aacute;s de la ausencia de color.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IM&Aacute;GENES DE SENSOR DE COLOR)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Vamos a construir a una persona (robot) que pueda llegar de su casa a su escuela. Para llevarlo de un lugar a otro debemos agregar el sensor de color. Vamos a utilizar el color verde y el rojo, con cada color vamos a programar un camino diferente. Su maestro(a) debe marcar en el suelo el punto de salida (casa) y el de llegada (escuela) y ustedes deber&aacute;n definir dos trayectorias o caminos distintos para llegar al destino. El camino puede ser tan largo o tan corto como ustedes decidan.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IMAGEN DE RECORRIDO)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para marcar los diferentes caminos pueden utilizar cinta de aislar de diferentes colores. Organ&iacute;cense en equipos, definan el camino y comiencen a cumplir el rol que les corresponde.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(TABLA DE HERRAMIENTA DE PROGRMACI&Oacute;N)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ed7d31\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Vamos a analizar nuestros desplazamientos y trayectorias. Mide la trayectoria y desplazamiento.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(TABLA DE REGISTRO DE RECORRIDOS)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Agrega otra trayectoria diferente usando el color amarillo y realiza la programaci&oacute;n necesaria para que el robot pueda seguirla</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-05 01:29:32', '2019-07-30 05:09:28'),
// (35, 'S2.1.6', 'CONSTRUYENDO CONOCIMIENTO', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Alguna vez has pensado en construir un robot que ayude a nuestra sociedad o a nuestro planeta?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">El d&iacute;a de hoy se va a construir uno a partir de los conocimientos adquiridos durante el bloque.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para realizar su robot deber&aacute;n de platicar en equipo y elegir un elemento de cada columna que se muestra a continuaci&oacute;n, es decir, deber&aacute;n elegir un sensor y una funci&oacute;n que su robot deber&aacute; tener. Cuando lo decidan deben colorear la celda que eligieron:</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(Tablas para colorear y elegir el sensor)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Escriban sus ideas, en qu&eacute; ayudar&aacute; y c&oacute;mo funcionar&aacute; el robot que decidieron hacer:</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Recuerden seguir los pasos de la ingenier&iacute;a</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ed7d31\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;El robot logra realizar la terea que pensaron?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; sensor utilizaron?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Si tuvieron complicaciones escribe c&oacute;mo las resolvieron.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Les fue f&aacute;cil crear un robot sin gu&iacute;a de ensamble?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Agrega un sensor adicional a tu ensamble.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>', NULL, NULL, NULL, '2019-07-05 01:46:36', '2019-07-30 05:11:10'),
// (36, 'S2.2.1', 'ENGRANAJES', NULL, NULL, '<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">Veamos el siguiente video</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Duraci&oacute;n 4:38 minutos</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">Disponible en </span></span></strong><strong><span style=\"font-size:12.0pt\"><span style=\"color:#ed1c24\"><a href=\"https://www.youtube.com/watch?v=kVTS8sLQUVI\" style=\"color:#0563c1; text-decoration:underline\" target=\"_blank\">https://www.youtube.com/watch?v=kVTS8sLQUVI</a></span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Qu&eacute; son los engranes?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Conoces los distintos tipos de engranes?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">En esta imagen podr&aacute;s observar algunos tipos de engranes</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(IM&Aacute;GENES DE TIPOS DE ENGRANES)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Para qu&eacute; se ocupan los engranes?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Los engranes son mecanismos utilizados para transmitir movimiento, adem&aacute;s de poder modificar la velocidad, fuerza o sentido del movimiento.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Cu&aacute;les son las principales caracter&iacute;sticas de los engranes?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para poder modificar la velocidad o fuerza de un mecanismo es necesario ocupar un tren de engranes.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Recuerdas c&oacute;mo debes acomodarlos para aumentar la fuerza? &iquest;Y la velocidad?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">En equipos de 3 personas deber&aacute;n ensamblar los principios b&aacute;sicos de los engranes.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Observen con atenci&oacute;n su funcionamiento y completen lo que se pide a continuaci&oacute;n</span></span><span style=\"font-size:14.0pt\"><span style=\"color:#201c20\">.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ed7d31\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para poder aumentar la velocidad de un mecanismo, es necesario colocar un engrane m&aacute;s ______ que mueva a uno m&aacute;s _______ Para poder aumentar la fuerza de un mecanismo, es necesario colocar un engrane m&aacute;s ______ Para poder modificar el sentido del movimiento se deben colocar _____ engranes.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;De qu&eacute; otra manera podr&iacute;as aumentar o disminuir la velocidad sin utilizar engranes?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">En base a lo observado y experimentado con los principios de engranes, construyan una batidora que sea lo m&aacute;s r&aacute;pido posible.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Si quieren pueden agregarle motores para su funcionamiento.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Engranaje: Mecanismo compuesto de una o varias ruedas dentadas que pueden transmitir movimiento y modificar la fuerza o velocidad.</span></span></span></span></p>', NULL, NULL, NULL, '2019-07-05 02:14:38', '2019-07-30 05:44:24'),
// (37, 'S2.2.2', 'LIEBRE Y LA TORTUGA', NULL, NULL, '<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#4040ab\">PREGUNTAR E IMAGINAR</span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Veamos el siguiente video</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Recreando Fabula Infantil Tortuga vs</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">LiebreTurtle vs Hare</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Duraci&oacute;n 0:39 minutos</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Disponible en: </span></span></strong><strong><span style=\"font-size:12.0pt\"><span style=\"color:#ed1c24\"><a href=\"https://www.youtube.com/watch?v=JaHnfAfVUw\" style=\"color:#0563c1; text-decoration:underline\" target=\"_blank\">https://www.youtube.com/watch?v=JaHnfAfVUw</a></span></span></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">En t&eacute;rminos generales, &iquest;A qui&eacute;n consideran m&aacute;s r&aacute;pida, a la liebre o a la tortuga? La tortuga hizo menos tiempo en llegar a la meta, &iquest;Piensan que ese competidor fue el m&aacute;s r&aacute;pido? &iquest;Por qu&eacute;?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Si la liebre tard&oacute; m&aacute;s tiempo en llegar a la meta, &iquest;significa que durante la carrera fue m&aacute;s lenta?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Para ustedes, &iquest;Qui&eacute;n fue la m&aacute;s r&aacute;pida de la carrera? Argumenten su respuesta.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">En la vida cotidiana escuchamos muchas veces las palabras velocidad y rapidez. &iquest;Qu&eacute; entienden por velocidad? &iquest;Es diferente a la rapidez?, &iquest;en qu&eacute;?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#00b050\">DISE&Ntilde;AR Y CONSTRUIR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;De qu&eacute; manera ocupando la misma potencia en dos ensambles diferentes podemos modificar su velocidad?</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Ocupando engranes realizaremos la carrera de la liebre y la tortuga. El ensamble ser&aacute; el mismo para todos los equipos y se programar&aacute; con la misma potencia (80), pero cada uno deber&aacute; modificarlo mec&aacute;nicamente para aumentar su velocidad y lograr ganar la carrera.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Completa la tabla de Herramientas de programaci&oacute;n de acuerdo a las diferentes acciones que debe llevar a cabo tu robot.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">(HERRAMIENTA DE PROGRAMACI&Oacute;N)</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ed7d31\">ANALIZAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Ya que tengan sus ensambles, realicen algunas competencias para ver cu&aacute;l es el m&aacute;s veloz.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;C&oacute;mo ayudan los engranes para ganar o reducir la velocidad de un ensamble?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;C&oacute;mo se consigue aumentar la velocidad?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Calculen la velocidad de sus ensambles ocupando la formula</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">V=d/t</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">&iquest;Cu&aacute;l es la velocidad de su ensamble?</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:red\">INNOVAR</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Ahora haremos una carrera de tortugas. i Ganar&aacute; la m&aacute;s lenta! Sin cambiar la potencia (80) realiza las modificaciones necesarias para que tu ensamble avance lo m&aacute;s lento posible.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">Calcula la velocidad de tu tortuga.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#201c20\">V=d/t</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:18.0pt\"><span style=\"color:#ffc000\">PARA RECORDAR</span></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, NULL, '2019-07-05 02:29:05', '2019-07-30 05:44:53');
